import React, { useState, useEffect } from 'react';

import Header from './components/header';
import Configurations from './components/configurations.js';
import FileProcessor from './components/fileprocessor.js';
import Sidebar from './components/sidebar.js';

import './App.css'
import axios from 'axios';

var XLSX = require("xlsx");

const App = () => {
  const [jsonData, setJsonData] = useState(null);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [activeComponent, setActiveComponent] = useState('configurations');
  const [userList, setUserList] = useState([])

  useEffect(()=> {
    axios
      .get('/api/users')
      .then((list) => {
        setUserList(list);
      })
      .catch(error => console.log('Error fetching users:', error))
  },[])

  const toggleComponent = (component) => {
    setActiveComponent(component);
  };

  const renderActiveComponent = () => {
    if (activeComponent === 'configurations') {
      return <Configurations onFileUpload={handleFileUpload} jsonData={jsonData}/>;
    } else if (activeComponent === 'fileProcessor') {
      return <FileProcessor />;
    }
  };

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();

    reader.onload = (e) => {
      const data = new Uint8Array(e.target.result);
      const workbook = XLSX.read(data, { type: 'array' });

      const sheetName = workbook.SheetNames[0];
      const worksheet = workbook.Sheets[sheetName];

      const parsedData = XLSX.utils.sheet_to_json(worksheet, { header: 1 });
      setJsonData(parsedData);
    };

    reader.readAsArrayBuffer(file);
  };

  const handleLogin = (event) => {
    event.preventDefault();
    console.log('logging in with', username, password)
    // const user = User.findOne({username})
    // console.log(user)
    console.log(userList)
  }

  return (
    (isLoggedIn) ?
    (<div className="app">
      <Sidebar toggleComponent={toggleComponent} />
      <div className="content">
        <Header />
        <div className="main">{renderActiveComponent()}</div>
      </div>
    </div>)
    : (<div className='login'>
        <form onSubmit={handleLogin}>
          <div>
            username: <input 
              type='text'
              value={username}
              name='username'
              onChange={({target})=>setUsername(target.value)}
            />
          </div>
          <div>
            password: <input 
              type='text'
              value={password}
              name='password'
              onChange={({target})=>setPassword(target.value)}
            />
          </div>
          <button type='submit'>Login</button>
        </form>
    </div>)
  );
};

export default App;
