import React, { useState } from 'react';
//import './parser.css'; // Assuming you have a corresponding CSS file for styling

const Parser = ({ onFileUpload, jsonData, onSaveConfiguration }) => {
  const [columnMappings, setColumnMappings] = useState({
    fullName: '',
    phoneNumber: '',
    emailAddress: '',
    party: '',
    age: '',
  });

  const handleColumnMapping = (e) => {
    const { name, value } = e.target;
    setColumnMappings((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  return (
    <div className="parser">
      <input type="file" onChange={onFileUpload} />
      {jsonData && (
        <div className="parsed-data">
          <h2>Column Mapping:</h2>
          <label htmlFor="fullName">Full Name:</label>
          <input
            type="text"
            name="fullName"
            value={columnMappings.fullName}
            onChange={handleColumnMapping}
          />
          <label htmlFor="phoneNumber">Phone Number:</label>
          <input
            type="text"
            name="phoneNumber"
            value={columnMappings.phoneNumber}
            onChange={handleColumnMapping}
          />
          <label htmlFor="emailAddress">Email Address:</label>
          <input
            type="text"
            name="emailAddress"
            value={columnMappings.emailAddress}
            onChange={handleColumnMapping}
          />
          <label htmlFor="party">Party:</label>
          <input
            type="text"
            name="party"
            value={columnMappings.party}
            onChange={handleColumnMapping}
          />
          <label htmlFor="age">Age:</label>
          <input
            type="text"
            name="age"
            value={columnMappings.age}
            onChange={handleColumnMapping}
          />
          <button onClick={() => onSaveConfiguration(columnMappings)}>
            Save Configuration
          </button>
        </div>
      )}
    </div>
  );
};

export default Parser;
